import React, { useState, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { solarizedlight } from 'react-syntax-highlighter/dist/esm/styles/prism';
import './MarkdownComponent.css';  // For styling the markdown

// Custom component to render code blocks with syntax highlighting
const CodeBlock = ({ language, value }) => {
  const handleCopyClick = () => {
    // Copy the code to the clipboard
    navigator.clipboard.writeText(value).then(() => {
      alert('Code copied to clipboard!');
    }).catch(err => {
      alert('Failed to copy code: ', err);
    });
  };

  return (
    <div className="code-block-container">
      <SyntaxHighlighter language={language || 'javascript'} style={solarizedlight}>
        {value}
      </SyntaxHighlighter>
      <button className="copy-button" onClick={handleCopyClick}>
        Copy Code
      </button>
    </div>
  );
};

function MarkdownComponent({ markdownFile }) {
  const [markdownContent, setMarkdownContent] = useState('');

  useEffect(() => {
    fetch(markdownFile)
      .then(response => response.text())
      .then(text => setMarkdownContent(text));
  }, [markdownFile]);

  return (
    <div className="markdown-main">
      {/* Render the markdown content using react-markdown */}
      <ReactMarkdown
        children={markdownContent}
        components={{
          code: ({ node, inline, className, children, ...props }) => {
            // Safe fallback for className check without optional chaining
            const language = className ? className.replace('language-', '') : null;
            return !inline ? (
              <CodeBlock language={language} value={String(children).replace(/\n$/, '')} />
            ) : (
              <code {...props}>{children}</code>
            );
          },
        }}
      />
    </div>
  );
}

export default MarkdownComponent;
