import React, { useState, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { solarizedlight } from 'react-syntax-highlighter/dist/esm/styles/prism';
import './MarkdownComponent.css';  // For styling the markdown
const CodeBlock = ({ language, value }) => {
  const [copyStatus, setCopyStatus] = useState('Copy Code'); // state to track button text

  const handleCopyClick = () => {
    // Copy the code to the clipboard
    navigator.clipboard.writeText(value)
      .then(() => {
        setCopyStatus('Copied'); // Change button text to 'Copied'
        // After 2 seconds, change the text back to 'Copy Code'
        setTimeout(() => {
          setCopyStatus('Copy Code');
        }, 2000);  // 2000ms = 2 seconds
      })
      .catch(err => {
        alert('Failed to copy code: ', err);  // If the copy fails, alert the user
      });
  };

  return (
    <div className="code-block-container">
      <SyntaxHighlighter language={language || 'javascript'} style={solarizedlight}>
        {value}
      </SyntaxHighlighter>
      <button className="copy-button" onClick={handleCopyClick}>
        {copyStatus} {/* Display the current button text */}
      </button>
    </div>
  );
};

